var server;this.onmessage=function(e){var t=e.data;switch(t.type){case"init":return startServer(t.defs,t.plugins,t.scripts);case"add":return server.addFile(t.name,t.text);case"del":return server.delFile(t.name);case"req":return server.request(t.body,function(e,r){postMessage({id:t.id,body:r,err:e&&String(e)})});case"getFile":var r=pending[t.id];delete pending[t.id];return r(t.err,t.text);default:throw new Error("Unknown message type: "+t.type)}};var nextId=0,pending={};function getFile(e,r){postMessage({type:"getFile",name:e,id:++nextId});pending[nextId]=r}function startServer(e,r,t){if(t)importScripts.apply(null,t);server=new tern.Server({getFile:getFile,async:true,defs:e,plugins:r})}var console={log:function(e){postMessage({type:"debug",message:e})}};